<script setup>
    import { useHeaderChannelStore } from '@/stores/header-channel'
    import { reactive } from 'vue'
    import { useAreaIn } from '@/js/mouse'

    const { channelLeft, more, channelRight } = useHeaderChannelStore()
    const channel = channelLeft.concat(more)
    let channels = new Array()
    let i = 0
    while(i < channel.length / 12){
        channels = channels.concat(channel.slice(i * 12, i * 12 + 12))
        channels = channels.concat(channelRight.slice(i * 3, i * 3 + 3))
        i++
    }
    const inHeaderChannelFixedArea = reactive({in: false})

</script>

