        letter-spacing: 2px;
        position: fixed;
        top: 100%;
        left: 0;
        display: flex;
        align-items: center;
        padding: 14px 140px;
        max-height: 56px;
        color: var(--text1);
        overflow: hidden;
        transition: all .2s;
    }
    .header-channel-fixed .channel-icons{
        align-self: flex-start;
        width: 160px;
        height: var(--row-height);
        display: flex;
        align-items: center;
    }
    .header-channel-fixed .channel-icons .icon{
        flex-grow: 1;
        display: flex;
        align-items: center;
    }
    .header-channel-fixed .channel-icons .icon:hover{
        color: var(--blue1);
    }
    .header-channel-fixed .channel-icons .icon .dynamic-bg, .header-channel-fixed .channel-icons .icon .popular-bg{
        line-height: 0;
        margin-right: 5px;
    }
    .header-channel-fixed .line{
        height: calc(var(--row-height) / 2);
        width: 1px;
        background-color: var(--Ga2);
        margin-right: 10px;
    }
    .header-channel-fixed .channels{
        align-self: flex-start;
        display: grid;
        grid-template-columns: repeat(15, 1fr);
        gap: 14px 10px;
        flex-grow: 1;
    }
    .header-channel-fixed .channels > a{
        text-align: center;
        border: 1px solid #F1F2F3;
        border-radius: 6px;
