        height: 28px;
        top: calc(var(--row-height) / 2);
        right: 100px;
        border-radius: 4px;
        line-height: 28px;
        text-align: center;
        transition: background-color .2s;
        cursor: pointer;
    }
    .header-channel-fixed .state-arrow:hover{
        background-color: var(--Ga2);
    }
    .header-channel-fixed .state-arrow svg{
        transition: rotate .2s;
    }
