<script setup>
    import { inject, reactive, ref, watch } from 'vue'
    import { useAreaIn } from '@/js/mouse'
    import { useUserStore } from '@/stores/user'
    import { storeToRefs } from 'pinia'

    const { exitLogin } = useUserStore()
    const { token, uid, uname, coin, bCoin, level, follower, following, dynamicCount } = storeToRefs(useUserStore())

    const inLoginArea = reactive({in: false})
    const inAvatarArea = reactive({in: false})
    const inUploadArea = reactive({in: false})
    const inMoreServiceArea = reactive({in: false})
    const moreServiceArea = ref(null)
    watch(moreServiceArea, (v) => {
        if(v){
            useAreaIn(v, inMoreServiceArea, 0)
        }
    })
    const openLoginOrRegisterPanel = inject('openLoginOrRegisterPanel')

    const avatar = ref(null)
    watch(avatar, v => {
        if(v){
            animation.effect = new KeyframeEffect(v, [{transform: 'scale(1) translateY(0)'}, {transform: 'scale(2.5) translateY(10px)'}], {
                duration: 350,
                delay: 100,
                fill: 'both',
                easing: 'ease-in'
            })
            //初始化,防止换号时动画停在变大
            animation.currentTime = 0
            animation.pause()
        }
    })
    const animation = new Animation()
    function inAvatar(){
        animation.updatePlaybackRate(1)
        animation.play()
    }
    function leaveAvatar(){
        animation.updatePlaybackRate(-1)
        animation.play()
    }
</script>

